import {
  Container,
  Group,
  ThemeIcon,
  Paper,
  Text,
  Title,
  Grid,
  Stack,
  Badge,
  Button,
} from "@mantine/core";
import { FaArrowRight } from "react-icons/fa";
import { Note } from "tabler-icons-react";

export default function TaskList() {
  let tasks = [
    {
      title: "Do shopping",
      description: "Get apples, oranges and apricots",
    },
    {
      title: "Complete homework",
      description: "Make an app to show tasks",
    },
    {
      title: "Blah blah blah",
      description: "Do something cool",
    },
  ];

  function getTasks() {
    return tasks.map((task) => (
      <Paper shadow="sm" p="lg">
        <Group style={{ float: "left" }} noWrap>
          <ThemeIcon variant="light" size="xl">
            <Note />
          </ThemeIcon>
          <div>
            <Group>
              <Title order={5}>{task.title}</Title>
              <Badge color="red">IMPORTANT</Badge>
            </Group>
            <Text size="sm" color="dimmed" weight={400}>
              {task.description}
            </Text>
          </div>
        </Group>
        <Button
          style={{ float: "right" }}
          leftIcon={<FaArrowRight />}
          color="red"
        >
          Remove
        </Button>
      </Paper>
    ));
  }

  return (
    <Container size="sm">
      <Stack>{getTasks()}</Stack>
    </Container>
  );
}
