import React from "react";

export default class CelsiusIndicator extends React.Component {
  constructor(props) {
    super(props);

    this.state = { temperature: this.props.value };

    this.onChange = this.onChange.bind(this);
  }

  onChange(event) {
    this.props.onChange(event.target.value, "c");
  }

  render() {
    return (
      <>
        <p>Temperature (Celsius):</p>
        <input
          type="number"
          value={this.state.temperature}
          onChange={this.onChange}
        ></input>
      </>
    );
  }
}
