import {
  Modal,
  Textarea,
  Group,
  TextInput,
  Switch,
  Button,
  Chips,
  Chip,
} from "@mantine/core";
import { useForm } from "@mantine/form";

export default function NewTaskModal(props) {
  const form = useForm({
    initialValues: {
      title: "",
      description: "",
      isImportant: false,
    },
  });

  function createNewTask(title, description) {
    props.addTask(title, description);
    props.setOpened(false);
    form.reset();
  }

  return (
    <Modal
      opened={props.opened}
      onClose={() => props.setOpened(false)}
      size="lg"
      title="Create a task..."
    >
      <form
        onSubmit={form.onSubmit((values) =>
          createNewTask(values.title, values.description)
        )}
      >
        <TextInput
          label="Title"
          type="text"
          variant="filled"
          placeholder="Title"
          {...form.getInputProps("title")}
          required
        ></TextInput>
        <Textarea
          label="Description"
          type="text"
          variant="filled"
          placeholder="Description"
          {...form.getInputProps("description")}
        ></Textarea>
        <Chips variant="filled" mt="md" multiple>
          <Chip value="important">Important</Chip>
        </Chips>
        <Chips
          position="center"
          multiple
          classNames={classes}
          defaultValue={["react"]}
        >
          <Chip value="react">React</Chip>
          <Chip value="ng">Angular</Chip>
          <Chip value="vue">Vue</Chip>
          <Chip value="svelte">Svelte</Chip>
        </Chips>
        <Group position="right" mt="md">
          <Button type="submit">Create</Button>
        </Group>
      </form>
    </Modal>
  );
}
