import { useState } from "react";
import { Modal, Textarea, TextInput, Stack, Button } from "@mantine/core";

export default function NewTaskModal(props) {
  return (
    <Modal
      opened={props.opened}
      onClose={() => props.setOpened(false)}
      size="lg"
      title="Create a task..."
    >
      <Stack>
        <TextInput
          label="Title"
          type="text"
          variant="filled"
          placeholder="Title"
          required
        ></TextInput>
        <Textarea
          label="Description"
          type="text"
          variant="filled"
          placeholder="Description"
        ></Textarea>
        <Button>Create</Button>
      </Stack>
    </Modal>
  );
}
