import {
  Container,
  Group,
  Grid,
  ThemeIcon,
  Accordion,
  Text,
  Badge,
  Button,
} from "@mantine/core";
import { Note } from "tabler-icons-react";

export default function TaskList() {
  let tasks = [
    {
      title: "Do shopping",
      description: "Get apples, oranges and apricots",
    },
    {
      title: "Complete homework",
      description: "Make an app to show tasks",
    },
    {
      title: "Blah blah blah",
      description: "Do something cool",
    },
  ];

  function AccordionLabel(title) {
    return (
      <>
        <Grid align="flex-start">
          <Grid.Col>
            <ThemeIcon variant="light" size="xl">
              <Note />
            </ThemeIcon>
          </Grid.Col>

          <Grid.Col>
            <Text>{title}</Text>
            <Text size="sm" color="dimmed" weight={400}>
              idk
            </Text>
          </Grid.Col>
          <Badge color="red">Important</Badge>
        </Grid>
      </>
    );
  }

  function getTasks() {
    return tasks.map((task) => (
      <Accordion.Item label={AccordionLabel(task.title)}>
        <Text>{task.description}</Text>
        <Button color="red">Remove</Button>
      </Accordion.Item>
    ));
  }

  return (
    <Container>
      <Accordion iconPosition="right">{getTasks()}</Accordion>
    </Container>
  );
}
