import { Modal, Textarea, TextInput, Stack, Button } from "@mantine/core";

export default function NewTaskModal() {
  const [opened, setOpened] = useState(true);

  return (
    <Modal
      opened={opened}
      onClose={setOpened(false)}
      size="lg"
      title="Create a task..."
    >
      <Stack>
        <TextInput
          label="Title"
          type="text"
          variant="filled"
          placeholder="Title"
          required
        ></TextInput>
        <Textarea
          label="Description"
          type="text"
          variant="filled"
          placeholder="Description"
        ></Textarea>
        <Button>Create</Button>
      </Stack>
    </Modal>
  );
}
