import React, { Component } from "react";

export default class FahrenheitIndicator extends React.Component {
  constructor(props) {
    super(props);

    this.state = { temperature: 0 };

    this.onChange = this.onChange.bind(this);
  }

  onChange(event) {
    this.setState({ temperature: event.target.value });
  }

  render() {
    return (
      <>
        <p>Temperature (Fahrenheit):</p>
        <input type="number" value={this.state.temperature}></input>
      </>
    );
  }
}
