import { Group, Paper, Text, Title, ActionIcon, Badge } from "@mantine/core";

import { FaEdit, FaTrash } from "react-icons/fa";

export default function TaskItem(props) {
  function renderTags() {
    return props.tags.map((tag) => (
      <Badge color="red" key={tag}>
        {tag}
      </Badge>
    ));
  }

  return (
    <Paper shadow="sm" p="lg" withBorder>
      <Group style={{ float: "left" }} noWrap>
        <div>
          <Group>
            <Title order={5}>{props.title}</Title>
            {renderTags()}
          </Group>
          <Text size="sm" color="dimmed" weight={400}>
            {props.description}
          </Text>
        </div>
      </Group>
      <Group style={{ float: "right" }}>
        <ActionIcon color="blue" size="lg" variant="light">
          <FaEdit />
        </ActionIcon>
        <ActionIcon
          color="red"
          size="lg"
          variant="light"
          onClick={() => props.deleteTask(key)}
        >
          <FaTrash />
        </ActionIcon>
      </Group>
    </Paper>
  );
}
