import { Modal, Textarea, Group, TextInput, Box, Button } from "@mantine/core";
import { useForm } from "@mantine/form";

export default function NewTaskModal(props) {
  function addTask() {}

  const form = useForm({
    initialValues: {},
  });

  return (
    <Modal
      opened={props.opened}
      onClose={() => props.setOpened(false)}
      size="lg"
      title="Create a task..."
    >
      <Box>
        <form onSubmit={() => addTask()}>
          <TextInput
            label="Title"
            type="text"
            variant="filled"
            placeholder="Title"
            required
          ></TextInput>
          <Textarea
            label="Description"
            type="text"
            variant="filled"
            placeholder="Description"
          ></Textarea>
          <Group position="right" mt="md">
            <Button type="submit">Create</Button>
          </Group>
        </form>
      </Box>
    </Modal>
  );
}
