import { Modal, Textarea, Group, TextInput, Box, Button } from "@mantine/core";
import { useForm } from "@mantine/form";

export default function NewTaskModal(props) {
  const form = useForm({
    initialValues: {
      title: "",
      description: "",
    },
  });

  function createNewTask(title, description) {
    props.addTask(title, description);
    props.setOpened(false);
    form.reset();
  }

  return (
    <Modal
      opened={props.opened}
      onClose={() => props.setOpened(false)}
      size="lg"
      title="Create a task..."
    >
      <form
        onSubmit={form.onSubmit((values) =>
          createNewTask(values.title, values.description)
        )}
      >
        <TextInput
          label="Title"
          type="text"
          variant="filled"
          placeholder="Title"
          {...form.getInputProps("title")}
          required
        ></TextInput>
        <Textarea
          label="Description"
          type="text"
          variant="filled"
          placeholder="Description"
          {...form.getInputProps("description")}
        ></Textarea>
        <Group position="right" mt="md">
          <Button type="submit">Create</Button>
        </Group>
      </form>
    </Modal>
  );
}
