import { useState } from "react";
import {
  MantineProvider,
  Container,
  Stack,
  Title,
  Button,
} from "@mantine/core";
import TaskList from "./components/TaskList";
import NewTaskModal from "./components/NewTaskModal";

export default function App() {
  const [opened, setOpened] = useState(false);
  const [tasks, setTasks] = useState([{ title: "hello", description: "yeet" }]);

  function addTask(title, description) {
    setTasks([{ title: "hello", description: "yeet" }]);
    //setTasks([{ title: title, description: description }]);
  }

  return (
    <MantineProvider>
      <Container size="sm">
        <Stack>
          <Title>My Tasks</Title>
          <TaskList tasks={tasks} />
          <Button onClick={() => setOpened(true)}>New Task</Button>
        </Stack>
      </Container>
      <NewTaskModal opened={opened} setOpened={setOpened} addTask={addTask} />
    </MantineProvider>
  );
}
