import {
  Modal,
  InputWrapper,
  Input,
  Textarea,
  Tooltip,
  Stack,
} from "@mantine/core";

export default function NewTaskModal() {
  return (
    <Modal opened={true} size="lg" title="Create a task..." centered>
      <Stack>
        <InputWrapper label="Title" required>
          <Input type="text" variant="filled" placeholder="Title"></Input>
        </InputWrapper>
        <Textarea
          label="Description"
          type="text"
          variant="filled"
          placeholder="Description"
        ></Textarea>
      </Stack>
    </Modal>
  );
}
