import {
  Group,
  Paper,
  Text,
  Title,
  ActionIcon,
  Stack,
  Badge,
} from "@mantine/core";
import { FaEdit, FaTrash } from "react-icons/fa";

export default function TaskList(props) {
  function getTasks() {
    return props.tasks.map((task, index) => (
      <Paper shadow="sm" p="lg" key={index} withBorder>
        <Group style={{ float: "left" }} noWrap>
          <div>
            <Group>
              <Title order={5}>{task.title}</Title>
              <Badge color="red">IMPORTANT</Badge>
            </Group>
            <Text size="sm" color="dimmed" weight={400}>
              {task.description}
            </Text>
          </div>
        </Group>
        <Group style={{ float: "right" }}>
          <ActionIcon color="blue" size="lg" variant="light">
            <FaEdit />
          </ActionIcon>
          <ActionIcon color="red" size="lg" variant="light">
            <FaTrash />
          </ActionIcon>
        </Group>
      </Paper>
    ));
  }

  return <Stack>{getTasks()}</Stack>;
}
