import {
  Modal,
  Textarea,
  Input,
  TextInput,
  Stack,
  Button,
} from "@mantine/core";
import { useForm } from "@mantine/form";

export default function NewTaskModal(props) {
  function addTask() {}

  const form = useForm({
    initialValues: {},
  });

  return (
    <Modal
      opened={props.opened}
      onClose={() => props.setOpened(false)}
      size="lg"
      title="Create a task..."
    >
      <Stack>
        <form onSubmit={() => addTask()}>
          <TextInput
            label="Title"
            type="text"
            variant="filled"
            placeholder="Title"
            required
          ></TextInput>
          <Textarea
            label="Description"
            type="text"
            variant="filled"
            placeholder="Description"
          ></Textarea>
          <Button type="submit" position="right">
            Create
          </Button>
        </form>
      </Stack>
    </Modal>
  );
}
