import { Modal, Input, Text, Tooltip, Stack } from "@mantine/core";
import { AlertCircle } from "tabler-icons-react";

export default function NewTaskModal() {
  return (
    <Modal opened={true} size="lg" title="Create a task..." centered>
      <Stack>
        <Text>Name</Text>
        <Input
          type="text"
          variant="filled"
          placeholder="Title"
          rightSection={
            <Tooltip label="Required" position="top" placement="end">
              <AlertCircle
                size={16}
                style={{ display: "block", opacity: 0.5 }}
              />
            </Tooltip>
          }
        ></Input>
        <Input type="text" variant="filled" placeholder="Description"></Input>
      </Stack>
    </Modal>
  );
}
