import {
  MantineProvider,
  Container,
  Stack,
  Title,
  Button,
} from "@mantine/core";
import TaskList from "./components/TaskList";
import NewTaskModal from "./components/NewTaskModal";

export default function App() {
  function createTask() {
    console.log("Task!");
  }

  return (
    <MantineProvider>
      <Container size="sm">
        <Stack>
          <Title>My Tasks</Title>
          <TaskList />
          <Button onClick={createTask}>New Task</Button>
        </Stack>
      </Container>
      <NewTaskModal />
    </MantineProvider>
  );
}
